insert into book values(0, '斗破苍穹', '天蚕土豆', '心潮澎湃，无限幻想，迎风挥击千层浪，少年不败热血！', '这里是属于斗气的世界，没有花俏艳丽的魔法，有的，仅仅是繁衍到巅峰的斗气！　　新书等级制度：斗者，斗师，大斗师，斗灵，斗王，斗皇，斗宗，斗尊，斗圣，斗帝。　　……　　吴磊、林允主演的同名电视剧9月3日起周一到周三每晚22：00登陆湖南卫视，腾讯视频全网独播。', 'http://bookcover.yuewen.com/qdbimg/349573/1209977/150', '15');
