insert into book values(0, '碾压诸天', '燃冷光', '【2020年科幻“超级科技”全国征文大赛】参赛作品', '不可抗拒的外力强制地球进入大宇宙时代。　　周哲带着神秘笔记本赐予的【绝对领悟】和【自我设定】天赋，开启了碾压诸天之旅！　　解析宇宙密码！破译超凡基因序列！揭晓硅基生命奥秘！　　搞事，搞事，搞事！　　老书《魔改全世界》、《从火影开始做主神》已完本，量大管饱，新书求收藏求推荐！', 'http://bookcover.yuewen.com/qdbimg/349573/1021578238/150', '2');
